import { StatusBar } from 'expo-status-bar';
import { StyleSheet, FlatList, Text, View } from 'react-native';
import React, {useState, useEffect} from 'react';
import langs from './langLookup.json';

langs = {
  "af": {
    "name": "Afrikaans",
    "translit": false
  },
  "am": {
    "name": "Amharic",
    "translit": true
  },
};

const langsArray = Object.keys(langs).map(key => ({
  langCode: key,
  ...langs[key],
}));

export default function App() {
  const [favorites, setFavorites] = useState([]);

  // useEffect(() => {
  //   setFavorites(data);
  // }, []);

  useEffect(() => {
    console.log(favorites);
  }, [favorites]);

  function handleFavorite(id) {
    const newFavorites = favorites.map(item => {
      return item.id === id ? { ...item, favorite: !item.favorite } : item;
    });

    setFavorites(newFavorites);
  }

  const langList =
        <FlatList
          data={langsArray}
          keyExtractor={(item, index) => {return index.toString()}}
          renderItem={({ item, index }) => {
            console.log("wassup " + item.name);
            <View key={index}>
              <Text>{item.name}</Text>
              <View style={styles.heart}>
                <Text style={{ color: 'red', fontSize: 24 }}>&#9829;</Text>
              </View>
            </View>
          }}
          ListHeaderComponent={() => <Text style={styles.title}>Languages</Text>}
        />
  return langList;
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 22,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  item: {
    padding: 10,
    fontSize: 18,
    backgroundColor: '#FF6B81'
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    textAlign: 'center',
    padding: 10,
  },
  heart: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#FF6B81',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 10,
  },
});
