import { StatusBar } from 'expo-status-bar';
import { StyleSheet, FlatList, Text, View, Pressable } from 'react-native';
import React, {useState, useEffect} from 'react';
import langs from './langLookup.json';


const langsArray = Object.keys(langs).map(key => ({
  langCode: key,
  ...langs[key],
}));


export default function App() {

  const [favoriteLangs, setFavoriteLang] = useState([]);
  const handleFavorite = (langCode) => {
    if (favoriteLangs.includes(langCode)) {
      setFavoriteLang(favoriteLangs.filter((i) => i !== langCode));
    } else {
      setFavorites([...favoriteLangs, langCode]);
    }
  }

  const langList =
      <View style={styles.container}>
        <FlatList
          data={langsArray}
          keyExtractor={item => item.langCode}
          renderItem={({item}) =>
          <Pressable style={styles.itemContainer} onPress={() => console.log('Pressed!')}>
            <View style={styles.buttonContainer}>
              <Text style={styles.item}>{item.name}</Text>
              <Pressable style={styles.heart} OnPress={() => handleFavorite(item.code)}>
                {favoriteLangs.includes(item.code) ? (
                  <Text style={{color: 'red', fontSize: 22}}>&#9829;</Text>
                ) : (
                  <Text style={{color: 'gray', fontSize: 22}}>&#9829;</Text>
                )}
              </Pressable>
            </View>
          </Pressable>
          }
          ListHeaderComponent={() => <Text style={styles.title}>Languages</Text>}
        />
      </View>
  return langList;
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 22
  },
  itemContainer: {
    width: 200,
    height: 50,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: '#ccc',
    marginHorizontal: 10,
    paddingVertical: 20,
    paddingHorizontal: 20,
    justifyContent: 'center'
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    textAlign: 'left',
    padding: 20,
  },
  heart: {
    alignSelf: 'flex-end',
    marginRight: 5
  },
  item: {
    color: 'black',
    alignSelf: 'flex-start',
    alignItems: 'center',
    marginLeft: 5
  }
});
